def partition(
    predicate: Callable[[Any], bool],
    iterator: Sequence[Any]
) -> Tuple[List[Any], List[Any]]:
    """A stable, out-of-place partition."""
    results = ([], [])
    for i in iterator:
        results[int(predicate(i))].append(i)
    # Returns trueList, falseList
    return results[1], results[0]

